import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title NexusToken
 * @dev ERC20 token for Nexus platform rewards
 * @notice Users earn NEX tokens for timely utility payments and good reputation
 */
contract NexusToken is ERC20, Ownable, Pausable {
    uint256 public constant MAX_SUPPLY = 1000000000 * 10**18; // 1 billion tokens
    uint256 public constant INITIAL_SUPPLY = 100000000 * 10**18; // 100 million tokens

    mapping(address => bool) public minters;

    event MinterAdded(address indexed minter);
    event MinterRemoved(address indexed minter);

    modifier onlyMinter() {
        require(minters[msg.sender] || msg.sender == owner(), "Not authorized minter");
        _;
    }

    constructor() ERC20("Nexus Token", "NEX") {
        _mint(msg.sender, INITIAL_SUPPLY);
        minters[msg.sender] = true;
    }

    /**
     * @dev Mint tokens to a specific address
     * @param _to Address to mint tokens to
     * @param _amount Amount of tokens to mint
     */
    function mint(address _to, uint256 _amount) external onlyMinter whenNotPaused {
        require(_to != address(0), "Cannot mint to zero address");
        require(totalSupply() + _amount <= MAX_SUPPLY, "Exceeds max supply");

        _mint(_to, _amount);
    }

    /**
     * @dev Add a minter address
     * @param _minter Address to add as minter
     */
    function addMinter(address _minter) external onlyOwner {
        require(_minter != address(0), "Invalid minter address");
        minters[_minter] = true;
        emit MinterAdded(_minter);
    }

    /**
     * @dev Remove a minter address
     * @param _minter Address to remove as minter
     */
    function removeMinter(address _minter) external onlyOwner {
        require(_minter != owner(), "Cannot remove owner");
        minters[_minter] = false;
        emit MinterRemoved(_minter);
    }

    /**
     * @dev Pause token transfers
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause token transfers
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev Override transfer to include pause functionality
     */
    function transfer(address to, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transfer(to, amount);
    }

    /**
     * @dev Override transferFrom to include pause functionality
     */
    function transferFrom(address from, address to, uint256 amount) public override whenNotPaused returns (bool) {
        return super.transferFrom(from, to, amount);
    }
}

contracts/ProfileRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @title ProfileRegistry
 * @dev Manages verified on-chain profiles for the Nexus platform
 * @notice Users can create profiles, submit verification documents, and get approved by admins/DAOs
 */
contract ProfileRegistry is Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _profileIds;

    enum ProfileStatus {
        Pending,
        Approved,
        Rejected,
        Suspended
    }

    struct Profile {
        uint256 id;
        address owner;
        string name;
        string email;
        string documentHash; // IPFS hash of verification documents
        ProfileStatus status;
        uint256 createdAt;
        uint256 updatedAt;
        address approvedBy; // Admin/DAO that approved the profile
        uint256 reputationScore;
        bool isActive;
    }

    struct VerificationDocument {
        string documentType; // "passport", "drivers_license", "utility_bill", etc.
        string documentHash;
        uint256 uploadedAt;
        bool isVerified;
    }

    mapping(uint256 => Profile) public profiles;
    mapping(address => uint256) public addressToProfileId;
    mapping(uint256 => VerificationDocument[]) public profileDocuments;
    mapping(address => bool) public admins;
    mapping(address => bool) public daos;

    uint256 public constant MIN_REPUTATION_SCORE = 100;
    uint256 public constant MAX_REPUTATION_SCORE = 1000;

    event ProfileCreated(uint256 indexed profileId, address indexed owner, string name);
    event ProfileUpdated(uint256 indexed profileId, address indexed owner);
    event ProfileApproved(uint256 indexed profileId, address indexed approver);
    event ProfileRejected(uint256 indexed profileId, address indexed rejector, string reason);
    event DocumentUploaded(uint256 indexed profileId, string documentType, string documentHash);
    event AdminAdded(address indexed admin);
    event AdminRemoved(address indexed admin);
    event DAOAdded(address indexed dao);
    event DAORemoved(address indexed dao);

    modifier onlyAdmin() {
        require(admins[msg.sender] || msg.sender == owner(), "Not an admin");
        _;
    }

    modifier onlyAdminOrDAO() {
        require(admins[msg.sender] || daos[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    modifier profileExists(uint256 _profileId) {
        require(_profileId > 0 && _profileId <= _profileIds.current(), "Profile does not exist");
        _;
    }

    modifier onlyProfileOwner(uint256 _profileId) {
        require(profiles[_profileId].owner == msg.sender, "Not profile owner");
        _;
    }

    constructor() {
        admins[msg.sender] = true;
    }

    /**
     * @dev Create a new profile
     * @param _name User's display name
     * @param _email User's email address
     * @param _documentHash IPFS hash of initial verification document
     */
    function createProfile(
        string memory _name,
        string memory _email,
        string memory _documentHash
    ) external nonReentrant returns (uint256) {
        require(addressToProfileId[msg.sender] == 0, "Profile already exists");
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(bytes(_email).length > 0, "Email cannot be empty");

        _profileIds.increment();
        uint256 newProfileId = _profileIds.current();

        profiles[newProfileId] = Profile({
            id: newProfileId,
            owner: msg.sender,
            name: _name,
            email: _email,
            documentHash: _documentHash,
            status: ProfileStatus.Pending,
            createdAt: block.timestamp,
            updatedAt: block.timestamp,
            approvedBy: address(0),
            reputationScore: 0,
            isActive: true
        });

        addressToProfileId[msg.sender] = newProfileId;

        emit ProfileCreated(newProfileId, msg.sender, _name);
        return newProfileId;
    }

    /**
     * @dev Update profile information
     * @param _profileId Profile ID to update
     * @param _name New name
     * @param _email New email
     */
    function updateProfile(
        uint256 _profileId,
        string memory _name,
        string memory _email
    ) external profileExists(_profileId) onlyProfileOwner(_profileId) {
        require(profiles[_profileId].status != ProfileStatus.Suspended, "Profile is suspended");
        require(bytes(_name).length > 0, "Name cannot be empty");
        require(bytes(_email).length > 0, "Email cannot be empty");

        profiles[_profileId].name = _name;
        profiles[_profileId].email = _email;
        profiles[_profileId].updatedAt = block.timestamp;

        emit ProfileUpdated(_profileId, msg.sender);
    }

    /**
     * @dev Upload verification document
     * @param _profileId Profile ID
     * @param _documentType Type of document
     * @param _documentHash IPFS hash of document
     */
    function uploadDocument(
        uint256 _profileId,
        string memory _documentType,
        string memory _documentHash
    ) external profileExists(_profileId) onlyProfileOwner(_profileId) {
        require(profiles[_profileId].status != ProfileStatus.Suspended, "Profile is suspended");
        require(bytes(_documentType).length > 0, "Document type cannot be empty");
        require(bytes(_documentHash).length > 0, "Document hash cannot be empty");

        profileDocuments[_profileId].push(VerificationDocument({
            documentType: _documentType,
            documentHash: _documentHash,
            uploadedAt: block.timestamp,
            isVerified: false
        }));

        emit DocumentUploaded(_profileId, _documentType, _documentHash);
    }

    /**
     * @dev Approve a profile (admin/DAO only)
     * @param _profileId Profile ID to approve
     */
    function approveProfile(uint256 _profileId) 
        external 
        profileExists(_profileId) 
        onlyAdminOrDAO 
    {
        require(profiles[_profileId].status == ProfileStatus.Pending, "Profile not pending");

        profiles[_profileId].status = ProfileStatus.Approved;
        profiles[_profileId].approvedBy = msg.sender;
        profiles[_profileId].updatedAt = block.timestamp;
        profiles[_profileId].reputationScore = MIN_REPUTATION_SCORE;

        emit ProfileApproved(_profileId, msg.sender);
    }

    /**
     * @dev Reject a profile (admin/DAO only)
     * @param _profileId Profile ID to reject
     * @param _reason Reason for rejection
     */
    function rejectProfile(uint256 _profileId, string memory _reason) 
        external 
        profileExists(_profileId) 
        onlyAdminOrDAO 
    {
        require(profiles[_profileId].status == ProfileStatus.Pending, "Profile not pending");

        profiles[_profileId].status = ProfileStatus.Rejected;
        profiles[_profileId].updatedAt = block.timestamp;

        emit ProfileRejected(_profileId, msg.sender, _reason);
    }

    /**
     * @dev Suspend a profile (admin only)
     * @param _profileId Profile ID to suspend
     */
    function suspendProfile(uint256 _profileId) 
        external 
        profileExists(_profileId) 
        onlyAdmin 
    {
        profiles[_profileId].status = ProfileStatus.Suspended;
        profiles[_profileId].updatedAt = block.timestamp;
        profiles[_profileId].isActive = false;

        emit ProfileRejected(_profileId, msg.sender, "Profile suspended");
    }

    /**
     * @dev Update reputation score
     * @param _profileId Profile ID
     * @param _newScore New reputation score
     */
    function updateReputationScore(uint256 _profileId, uint256 _newScore) 
        external 
        onlyAdminOrDAO 
        profileExists(_profileId) 
    {
        require(_newScore >= 0 && _newScore <= MAX_REPUTATION_SCORE, "Invalid reputation score");

        profiles[_profileId].reputationScore = _newScore;
        profiles[_profileId].updatedAt = block.timestamp;

        if (_newScore < MIN_REPUTATION_SCORE) {
            profiles[_profileId].isActive = false;
        } else {
            profiles[_profileId].isActive = true;
        }
    }

    /**
     * @dev Add admin
     * @param _admin Address to add as admin
     */
    function addAdmin(address _admin) external onlyOwner {
        require(_admin != address(0), "Invalid admin address");
        admins[_admin] = true;
        emit AdminAdded(_admin);
    }

    /**
     * @dev Remove admin
     * @param _admin Address to remove as admin
     */
    function removeAdmin(address _admin) external onlyOwner {
        require(_admin != owner(), "Cannot remove owner");
        admins[_admin] = false;
        emit AdminRemoved(_admin);
    }

    /**
     * @dev Add DAO
     * @param _dao Address to add as DAO
     */
    function addDAO(address _dao) external onlyOwner {
        require(_dao != address(0), "Invalid DAO address");
        daos[_dao] = true;
        emit DAOAdded(_dao);
    }

    /**
     * @dev Remove DAO
     * @param _dao Address to remove as DAO
     */
    function removeDAO(address _dao) external onlyOwner {
        daos[_dao] = false;
        emit DAORemoved(_dao);
    }

    /**
     * @dev Get profile by address
     * @param _address User address
     * @return Profile data
     */
    function getProfileByAddress(address _address) external view returns (Profile memory) {
        uint256 profileId = addressToProfileId[_address];
        require(profileId > 0, "Profile not found");
        return profiles[profileId];
    }

    /**
     * @dev Get profile documents
     * @param _profileId Profile ID
     * @return Array of verification documents
     */
    function getProfileDocuments(uint256 _profileId) 
        external 
        view 
        profileExists(_profileId) 
        returns (VerificationDocument[] memory) 
    {
        return profileDocuments[_profileId];
    }

    /**
     * @dev Check if address has verified profile
     * @param _address User address
     * @return True if verified
     */
    function isVerified(address _address) external view returns (bool) {
        uint256 profileId = addressToProfileId[_address];
        if (profileId == 0) return false;

        Profile memory profile = profiles[profileId];
        return profile.status == ProfileStatus.Approved && profile.isActive;
    }

    /**
     * @dev Get total number of profiles
     * @return Total profile count
     */
    function getTotalProfiles() external view returns (uint256) {
        return _profileIds.current();
    }
}

contracts/ReputationSystem.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./ProfileRegistry.sol";
import "./UtilityPayment.sol";

/**
 * @title ReputationSystem
 * @dev Manages reputation scores and DAO/admin approval system for the Nexus platform
 * @notice Builds a transparent reputation network through community governance
 */
contract ReputationSystem is Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _proposalIds;

    ProfileRegistry public profileRegistry;
    UtilityPayment public utilityPayment;

    enum ProposalType {
        ProfileApproval,
        ProfileSuspension,
        AdminAddition,
        AdminRemoval,
        DAOAddition,
        DAORemoval,
        ParameterUpdate
    }

    enum ProposalStatus {
        Active,
        Executed,
        Rejected,
        Expired
    }

    struct Proposal {
        uint256 id;
        ProposalType proposalType;
        address proposer;
        address targetAddress; // Address being voted on (for admin/DAO proposals)
        string description;
        string metadata; // Additional proposal data
        uint256 votesFor;
        uint256 votesAgainst;
        uint256 startTime;
        uint256 endTime;
        ProposalStatus status;
        bool executed;
        mapping(address => bool) hasVoted;
        mapping(address => uint256) votingPower;
    }

    struct ReputationMetrics {
        uint256 profileScore;
        uint256 paymentScore;
        uint256 communityScore;
        uint256 totalScore;
        uint256 lastUpdated;
        uint256 level; // Reputation level (1-10)
    }

    mapping(uint256 => Proposal) public proposals;
    mapping(address => ReputationMetrics) public reputationMetrics;
    mapping(address => bool) public daos;
    mapping(address => bool) public admins;
    mapping(address => uint256) public daoStakes; // DAO voting power based on stake

    // Voting parameters
    uint256 public constant VOTING_DURATION = 7 days;
    uint256 public constant MIN_PROPOSAL_THRESHOLD = 1000; // Minimum reputation to create proposal
    uint256 public constant QUORUM_THRESHOLD = 30; // 30% of total stake required
    uint256 public constant APPROVAL_THRESHOLD = 60; // 60% approval required

    // Reputation scoring weights
    uint256 public profileWeight = 30;
    uint256 public paymentWeight = 40;
    uint256 public communityWeight = 30;

    // Reputation levels
    uint256[] public reputationLevels = [100, 200, 350, 500, 700, 900, 1200, 1500, 2000, 2500];

    event ProposalCreated(uint256 indexed proposalId, ProposalType proposalType, address indexed proposer, string description);
    event VoteCast(uint256 indexed proposalId, address indexed voter, bool support, uint256 votingPower);
    event ProposalExecuted(uint256 indexed proposalId, ProposalType proposalType);
    event ReputationUpdated(address indexed user, uint256 newScore, uint256 newLevel);
    event DAOStaked(address indexed dao, uint256 amount);
    event DAOUnstaked(address indexed dao, uint256 amount);
    event AdminAdded(address indexed admin);
    event AdminRemoved(address indexed admin);
    event DAOAdded(address indexed dao);
    event DAORemoved(address indexed dao);

    modifier onlyDAO() {
        require(daos[msg.sender], "Not a DAO");
        _;
    }

    modifier onlyAdmin() {
        require(admins[msg.sender] || msg.sender == owner(), "Not an admin");
        _;
    }

    modifier proposalExists(uint256 _proposalId) {
        require(_proposalId > 0 && _proposalId <= _proposalIds.current(), "Proposal does not exist");
        _;
    }

    modifier onlyVerifiedUser() {
        require(profileRegistry.isVerified(msg.sender), "User not verified");
        _;
    }

    constructor(address _profileRegistry, address _utilityPayment) {
        profileRegistry = ProfileRegistry(_profileRegistry);
        utilityPayment = UtilityPayment(_utilityPayment);
        admins[msg.sender] = true;
    }

    /**
     * @dev Create a new proposal
     * @param _proposalType Type of proposal
     * @param _targetAddress Address being voted on (if applicable)
     * @param _description Proposal description
     * @param _metadata Additional proposal data
     */
    function createProposal(
        ProposalType _proposalType,
        address _targetAddress,
        string memory _description,
        string memory _metadata
    ) external onlyVerifiedUser nonReentrant returns (uint256) {
        ReputationMetrics memory metrics = reputationMetrics[msg.sender];
        require(metrics.totalScore >= MIN_PROPOSAL_THRESHOLD, "Insufficient reputation to create proposal");
        require(bytes(_description).length > 0, "Description cannot be empty");

        _proposalIds.increment();
        uint256 newProposalId = _proposalIds.current();

        Proposal storage proposal = proposals[newProposalId];
        proposal.id = newProposalId;
        proposal.proposalType = _proposalType;
        proposal.proposer = msg.sender;
        proposal.targetAddress = _targetAddress;
        proposal.description = _description;
        proposal.metadata = _metadata;
        proposal.startTime = block.timestamp;
        proposal.endTime = block.timestamp + VOTING_DURATION;
        proposal.status = ProposalStatus.Active;
        proposal.executed = false;

        emit ProposalCreated(newProposalId, _proposalType, msg.sender, _description);
        return newProposalId;
    }

    /**
     * @dev Vote on a proposal
     * @param _proposalId Proposal ID
     * @param _support True for yes, false for no
     */
    function vote(uint256 _proposalId, bool _support) 
        external 
        proposalExists(_proposalId) 
        onlyVerifiedUser 
        nonReentrant 
    {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.status == ProposalStatus.Active, "Proposal not active");
        require(block.timestamp <= proposal.endTime, "Voting period ended");
        require(!proposal.hasVoted[msg.sender], "Already voted");

        uint256 votingPower = calculateVotingPower(msg.sender);
        require(votingPower > 0, "No voting power");

        proposal.hasVoted[msg.sender] = true;
        proposal.votingPower[msg.sender] = votingPower;

        if (_support) {
            proposal.votesFor += votingPower;
        } else {
            proposal.votesAgainst += votingPower;
        }

        emit VoteCast(_proposalId, msg.sender, _support, votingPower);
    }

    /**
     * @dev Execute a proposal
     * @param _proposalId Proposal ID
     */
    function executeProposal(uint256 _proposalId) 
        external 
        proposalExists(_proposalId) 
        nonReentrant 
    {
        Proposal storage proposal = proposals[_proposalId];
        require(proposal.status == ProposalStatus.Active, "Proposal not active");
        require(block.timestamp > proposal.endTime, "Voting period not ended");
        require(!proposal.executed, "Proposal already executed");

        uint256 totalVotes = proposal.votesFor + proposal.votesAgainst;
        uint256 totalStake = getTotalStake();

        // Check quorum
        require(totalVotes >= (totalStake * QUORUM_THRESHOLD) / 100, "Quorum not met");

        // Check approval threshold
        bool approved = proposal.votesFor > (totalVotes * APPROVAL_THRESHOLD) / 100;

        if (approved) {
            proposal.status = ProposalStatus.Executed;
            proposal.executed = true;

            // Execute the proposal based on type
            _executeProposal(proposal);

            emit ProposalExecuted(_proposalId, proposal.proposalType);
        } else {
            proposal.status = ProposalStatus.Rejected;
        }
    }

    /**
     * @dev Execute proposal actions
     * @param _proposal Proposal to execute
     */
    function _executeProposal(Proposal storage _proposal) internal {
        if (_proposal.proposalType == ProposalType.ProfileApproval) {
            profileRegistry.approveProfile(profileRegistry.addressToProfileId(_proposal.targetAddress));
        } else if (_proposal.proposalType == ProposalType.ProfileSuspension) {
            profileRegistry.suspendProfile(profileRegistry.addressToProfileId(_proposal.targetAddress));
        } else if (_proposal.proposalType == ProposalType.AdminAddition) {
            profileRegistry.addAdmin(_proposal.targetAddress);
            admins[_proposal.targetAddress] = true;
            emit AdminAdded(_proposal.targetAddress);
        } else if (_proposal.proposalType == ProposalType.AdminRemoval) {
            profileRegistry.removeAdmin(_proposal.targetAddress);
            admins[_proposal.targetAddress] = false;
            emit AdminRemoved(_proposal.targetAddress);
        } else if (_proposal.proposalType == ProposalType.DAOAddition) {
            profileRegistry.addDAO(_proposal.targetAddress);
            daos[_proposal.targetAddress] = true;
            emit DAOAdded(_proposal.targetAddress);
        } else if (_proposal.proposalType == ProposalType.DAORemoval) {
            profileRegistry.removeDAO(_proposal.targetAddress);
            daos[_proposal.targetAddress] = false;
            emit DAORemoved(_proposal.targetAddress);
        }
    }

    /**
     * @dev Calculate voting power for a user
     * @param _user User address
     * @return Voting power
     */
    function calculateVotingPower(address _user) public view returns (uint256) {
        ReputationMetrics memory metrics = reputationMetrics[_user];

        // Base voting power from reputation
        uint256 basePower = metrics.totalScore;

        // DAO staking bonus
        if (daos[_user]) {
            basePower += daoStakes[_user];
        }

        return basePower;
    }

    /**
     * @dev Update user reputation metrics
     * @param _user User address
     */
    function updateReputationMetrics(address _user) external {
        require(profileRegistry.isVerified(_user), "User not verified");

        ReputationMetrics storage metrics = reputationMetrics[_user];

        // Get profile data
        ProfileRegistry.Profile memory profile = profileRegistry.getProfileByAddress(_user);
        metrics.profileScore = profile.reputationScore;

        // Get payment history
        UtilityPayment.PaymentHistory memory paymentHistory = utilityPayment.getPaymentHistory(_user);
        metrics.paymentScore = _calculatePaymentScore(paymentHistory);

        // Community score (simplified - in real implementation, this would include more factors)
        metrics.communityScore = _calculateCommunityScore(_user);

        // Calculate total score
        metrics.totalScore = (metrics.profileScore * profileWeight) / 100 +
                            (metrics.paymentScore * paymentWeight) / 100 +
                            (metrics.communityScore * communityWeight) / 100;

        // Update reputation level
        metrics.level = _calculateReputationLevel(metrics.totalScore);
        metrics.lastUpdated = block.timestamp;

        emit ReputationUpdated(_user, metrics.totalScore, metrics.level);
    }

    /**
     * @dev Calculate payment score based on payment history
     * @param _paymentHistory Payment history data
     * @return Payment score
     */
    function _calculatePaymentScore(UtilityPayment.PaymentHistory memory _paymentHistory) 
        internal 
        pure 
        returns (uint256) 
    {
        if (_paymentHistory.totalPayments == 0) return 0;

        uint256 onTimeRate = (_paymentHistory.onTimePayments * 100) / _paymentHistory.totalPayments;
        uint256 baseScore = _paymentHistory.totalPayments * 10; // Base score per payment

        // Bonus for on-time payments
        if (onTimeRate >= 90) {
            return baseScore * 2; // 200% bonus
        } else if (onTimeRate >= 80) {
            return (baseScore * 150) / 100; // 150% bonus
        } else if (onTimeRate >= 70) {
            return (baseScore * 120) / 100; // 120% bonus
        }

        return baseScore;
    }

    /**
     * @dev Calculate community score
     * @param _user User address
     * @return Community score
     */
    function _calculateCommunityScore(address _user) internal view returns (uint256) {
        // Simplified community score calculation
        // In a real implementation, this would include:
        // - Number of proposals created
        // - Number of votes cast
        // - Community contributions
        // - Social interactions

        uint256 score = 0;

        // Check if user has created proposals
        for (uint256 i = 1; i <= _proposalIds.current(); i++) {
            if (proposals[i].proposer == _user) {
                score += 50;
            }
        }

        // Check if user has voted on proposals
        for (uint256 i = 1; i <= _proposalIds.current(); i++) {
            if (proposals[i].hasVoted[_user]) {
                score += 10;
            }
        }

        return score;
    }

    /**
     * @dev Calculate reputation level based on total score
     * @param _totalScore Total reputation score
     * @return Reputation level (1-10)
     */
    function _calculateReputationLevel(uint256 _totalScore) internal view returns (uint256) {
        for (uint256 i = 0; i < reputationLevels.length; i++) {
            if (_totalScore < reputationLevels[i]) {
                return i + 1;
            }
        }
        return 10; // Maximum level
    }

    /**
     * @dev Stake tokens as DAO for increased voting power
     * @param _amount Amount to stake
     */
    function stakeAsDAO(uint256 _amount) external payable onlyDAO {
        require(_amount > 0, "Amount must be greater than 0");
        require(msg.value >= _amount, "Insufficient payment");

        daoStakes[msg.sender] += _amount;
        emit DAOStaked(msg.sender, _amount);
    }

    /**
     * @dev Unstake tokens as DAO
     * @param _amount Amount to unstake
     */
    function unstakeAsDAO(uint256 _amount) external onlyDAO {
        require(_amount > 0, "Amount must be greater than 0");
        require(daoStakes[msg.sender] >= _amount, "Insufficient stake");

        daoStakes[msg.sender] -= _amount;
        payable(msg.sender).transfer(_amount);
        emit DAOUnstaked(msg.sender, _amount);
    }

    /**
     * @dev Get total stake in the system
     * @return Total stake amount
     */
    function getTotalStake() public view returns (uint256) {
        uint256 total = 0;
        for (uint256 i = 1; i <= _proposalIds.current(); i++) {
            // This is simplified - in real implementation, you'd track total stake differently
            total += 1000; // Placeholder
        }
        return total;
    }

    /**
     * @dev Get user's reputation metrics
     * @param _user User address
     * @return Reputation metrics
     */
    function getReputationMetrics(address _user) external view returns (ReputationMetrics memory) {
        return reputationMetrics[_user];
    }

    /**
     * @dev Get proposal details
     * @param _proposalId Proposal ID
     */
    function getProposal(uint256 _proposalId) 
        external 
        view 
        proposalExists(_proposalId) 
        returns (
            uint256 id,
            ProposalType proposalType,
            address proposer,
            address targetAddress,
            string memory description,
            string memory metadata,
            uint256 votesFor,
            uint256 votesAgainst,
            uint256 startTime,
            uint256 endTime,
            ProposalStatus status,
            bool executed
        ) 
    {
        Proposal storage proposal = proposals[_proposalId];
        return (
            proposal.id,
            proposal.proposalType,
            proposal.proposer,
            proposal.targetAddress,
            proposal.description,
            proposal.metadata,
            proposal.votesFor,
            proposal.votesAgainst,
            proposal.startTime,
            proposal.endTime,
            proposal.status,
            proposal.executed
        );
    }

    /**
     * @dev Update reputation scoring weights
     * @param _profileWeight Profile weight percentage
     * @param _paymentWeight Payment weight percentage
     * @param _communityWeight Community weight percentage
     */
    function updateScoringWeights(
        uint256 _profileWeight,
        uint256 _paymentWeight,
        uint256 _communityWeight
    ) external onlyOwner {
        require(_profileWeight + _paymentWeight + _communityWeight == 100, "Weights must sum to 100");

        profileWeight = _profileWeight;
        paymentWeight = _paymentWeight;
        communityWeight = _communityWeight;
    }

    /**
     * @dev Get total proposals count
     * @return Total proposal count
     */
    function getTotalProposals() external view returns (uint256) {
        return _proposalIds.current();
    }
}

contracts/UtilityPayment.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "./ProfileRegistry.sol";

/**
 * @title UtilityPayment
 * @dev Handles utility bill payments and reward distribution for timely payments
 * @notice Users can pay utility bills and earn rewards for maintaining good payment history
 */
contract UtilityPayment is Ownable, ReentrancyGuard {
    using Counters for Counters.Counter;

    Counters.Counter private _paymentIds;
    Counters.Counter private _billIds;

    ProfileRegistry public profileRegistry;
    IERC20 public nexusToken; // Reward token

    enum PaymentStatus {
        Pending,
        Completed,
        Failed,
        Refunded
    }

    enum BillType {
        Electricity,
        Water,
        Gas,
        Internet,
        Phone,
        Rent,
        Insurance,
        Other
    }

    struct UtilityBill {
        uint256 billId;
        address user;
        BillType billType;
        string provider;
        uint256 amount;
        uint256 dueDate;
        uint256 createdAt;
        bool isPaid;
        bool isOverdue;
        string metadata; // Additional bill information
    }

    struct Payment {
        uint256 paymentId;
        uint256 billId;
        address user;
        uint256 amount;
        uint256 paidAt;
        PaymentStatus status;
        string transactionHash;
        uint256 rewardEarned;
    }

    struct PaymentHistory {
        uint256 totalPayments;
        uint256 onTimePayments;
        uint256 overduePayments;
        uint256 totalRewardsEarned;
        uint256 lastPaymentDate;
    }

    mapping(uint256 => UtilityBill) public bills;
    mapping(uint256 => Payment) public payments;
    mapping(address => uint256[]) public userBills;
    mapping(address => uint256[]) public userPayments;
    mapping(address => PaymentHistory) public paymentHistories;
    mapping(address => bool) public authorizedProviders;

    // Reward configuration
    uint256 public onTimeRewardRate = 5; // 5% of bill amount
    uint256 public earlyPaymentBonus = 2; // Additional 2% for payments 7+ days early
    uint256 public constant EARLY_PAYMENT_THRESHOLD = 7 days;
    uint256 public constant OVERDUE_PENALTY_DAYS = 30 days;

    // Fee configuration
    uint256 public platformFeeRate = 1; // 1% platform fee
    address public feeRecipient;

    event BillCreated(uint256 indexed billId, address indexed user, BillType billType, uint256 amount, uint256 dueDate);
    event PaymentMade(uint256 indexed paymentId, uint256 indexed billId, address indexed user, uint256 amount, uint256 rewardEarned);
    event RewardDistributed(address indexed user, uint256 amount);
    event ProviderAuthorized(address indexed provider);
    event ProviderDeauthorized(address indexed provider);
    event RewardRateUpdated(uint256 newRate);
    event PlatformFeeUpdated(uint256 newFeeRate);

    modifier onlyAuthorizedProvider() {
        require(authorizedProviders[msg.sender] || msg.sender == owner(), "Not authorized provider");
        _;
    }

    modifier billExists(uint256 _billId) {
        require(_billId > 0 && _billId <= _billIds.current(), "Bill does not exist");
        _;
    }

    modifier paymentExists(uint256 _paymentId) {
        require(_paymentId > 0 && _paymentId <= _paymentIds.current(), "Payment does not exist");
        _;
    }

    modifier onlyVerifiedUser() {
        require(profileRegistry.isVerified(msg.sender), "User not verified");
        _;
    }

    constructor(
        address _profileRegistry,
        address _nexusToken,
        address _feeRecipient
    ) {
        profileRegistry = ProfileRegistry(_profileRegistry);
        nexusToken = IERC20(_nexusToken);
        feeRecipient = _feeRecipient;
        authorizedProviders[msg.sender] = true;
    }

    /**
     * @dev Create a new utility bill
     * @param _user User address
     * @param _billType Type of utility bill
     * @param _provider Service provider name
     * @param _amount Bill amount
     * @param _dueDate Due date timestamp
     * @param _metadata Additional bill information
     */
    function createBill(
        address _user,
        BillType _billType,
        string memory _provider,
        uint256 _amount,
        uint256 _dueDate,
        string memory _metadata
    ) external onlyAuthorizedProvider returns (uint256) {
        require(_user != address(0), "Invalid user address");
        require(_amount > 0, "Amount must be greater than 0");
        require(_dueDate > block.timestamp, "Due date must be in the future");
        require(bytes(_provider).length > 0, "Provider name cannot be empty");

        _billIds.increment();
        uint256 newBillId = _billIds.current();

        bills[newBillId] = UtilityBill({
            billId: newBillId,
            user: _user,
            billType: _billType,
            provider: _provider,
            amount: _amount,
            dueDate: _dueDate,
            createdAt: block.timestamp,
            isPaid: false,
            isOverdue: false,
            metadata: _metadata
        });

        userBills[_user].push(newBillId);

        emit BillCreated(newBillId, _user, _billType, _amount, _dueDate);
        return newBillId;
    }

    /**
     * @dev Pay a utility bill
     * @param _billId Bill ID to pay
     */
    function payBill(uint256 _billId) 
        external 
        payable 
        billExists(_billId) 
        onlyVerifiedUser 
        nonReentrant 
    {
        UtilityBill storage bill = bills[_billId];
        require(bill.user == msg.sender, "Not bill owner");
        require(!bill.isPaid, "Bill already paid");
        require(msg.value >= bill.amount, "Insufficient payment amount");

        // Calculate platform fee
        uint256 platformFee = (bill.amount * platformFeeRate) / 100;
        uint256 netAmount = bill.amount - platformFee;

        // Calculate reward
        uint256 rewardAmount = calculateReward(_billId);

        // Update bill status
        bill.isPaid = true;

        // Create payment record
        _paymentIds.increment();
        uint256 newPaymentId = _paymentIds.current();

        payments[newPaymentId] = Payment({
            paymentId: newPaymentId,
            billId: _billId,
            user: msg.sender,
            amount: bill.amount,
            paidAt: block.timestamp,
            status: PaymentStatus.Completed,
            transactionHash: "",
            rewardEarned: rewardAmount
        });

        userPayments[msg.sender].push(newPaymentId);

        // Update payment history
        PaymentHistory storage history = paymentHistories[msg.sender];
        history.totalPayments++;
        history.lastPaymentDate = block.timestamp;

        if (block.timestamp <= bill.dueDate) {
            history.onTimePayments++;
        } else {
            history.overduePayments++;
        }

        if (rewardAmount > 0) {
            history.totalRewardsEarned += rewardAmount;
            // Transfer reward tokens to user
            require(nexusToken.transfer(msg.sender, rewardAmount), "Reward transfer failed");
            emit RewardDistributed(msg.sender, rewardAmount);
        }

        // Transfer payment to provider (simplified - in real implementation, this would go to actual provider)
        payable(owner()).transfer(netAmount);

        // Transfer platform fee
        if (platformFee > 0) {
            payable(feeRecipient).transfer(platformFee);
        }

        // Refund excess payment
        if (msg.value > bill.amount) {
            payable(msg.sender).transfer(msg.value - bill.amount);
        }

        emit PaymentMade(newPaymentId, _billId, msg.sender, bill.amount, rewardAmount);
    }

    /**
     * @dev Calculate reward for a payment
     * @param _billId Bill ID
     * @return Reward amount
     */
    function calculateReward(uint256 _billId) public view billExists(_billId) returns (uint256) {
        UtilityBill memory bill = bills[_billId];

        if (block.timestamp > bill.dueDate + OVERDUE_PENALTY_DAYS) {
            return 0; // No reward for severely overdue payments
        }

        uint256 baseReward = (bill.amount * onTimeRewardRate) / 100;

        // Early payment bonus
        if (block.timestamp <= bill.dueDate - EARLY_PAYMENT_THRESHOLD) {
            uint256 earlyBonus = (bill.amount * earlyPaymentBonus) / 100;
            return baseReward + earlyBonus;
        }

        // On-time payment
        if (block.timestamp <= bill.dueDate) {
            return baseReward;
        }

        // Overdue payment (reduced reward)
        return baseReward / 2;
    }

    /**
     * @dev Mark bill as overdue
     * @param _billId Bill ID
     */
    function markBillOverdue(uint256 _billId) external onlyAuthorizedProvider billExists(_billId) {
        UtilityBill storage bill = bills[_billId];
        require(!bill.isPaid, "Bill already paid");
        require(block.timestamp > bill.dueDate, "Bill not yet overdue");

        bill.isOverdue = true;
    }

    /**
     * @dev Get user's payment history
     * @param _user User address
     * @return Payment history data
     */
    function getPaymentHistory(address _user) external view returns (PaymentHistory memory) {
        return paymentHistories[_user];
    }

    /**
     * @dev Get user's bills
     * @param _user User address
     * @return Array of bill IDs
     */
    function getUserBills(address _user) external view returns (uint256[] memory) {
        return userBills[_user];
    }

    /**
     * @dev Get user's payments
     * @param _user User address
     * @return Array of payment IDs
     */
    function getUserPayments(address _user) external view returns (uint256[] memory) {
        return userPayments[_user];
    }

    /**
     * @dev Get bill details
     * @param _billId Bill ID
     * @return Bill data
     */
    function getBill(uint256 _billId) external view billExists(_billId) returns (UtilityBill memory) {
        return bills[_billId];
    }

    /**
     * @dev Get payment details
     * @param _paymentId Payment ID
     * @return Payment data
     */
    function getPayment(uint256 _paymentId) external view paymentExists(_paymentId) returns (Payment memory) {
        return payments[_paymentId];
    }

    /**
     * @dev Authorize a provider
     * @param _provider Provider address
     */
    function authorizeProvider(address _provider) external onlyOwner {
        require(_provider != address(0), "Invalid provider address");
        authorizedProviders[_provider] = true;
        emit ProviderAuthorized(_provider);
    }

    /**
     * @dev Deauthorize a provider
     * @param _provider Provider address
     */
    function deauthorizeProvider(address _provider) external onlyOwner {
        authorizedProviders[_provider] = false;
        emit ProviderDeauthorized(_provider);
    }

    /**
     * @dev Update reward rate
     * @param _newRate New reward rate (percentage)
     */
    function updateRewardRate(uint256 _newRate) external onlyOwner {
        require(_newRate <= 20, "Reward rate too high"); // Max 20%
        onTimeRewardRate = _newRate;
        emit RewardRateUpdated(_newRate);
    }

    /**
     * @dev Update platform fee rate
     * @param _newFeeRate New fee rate (percentage)
     */
    function updatePlatformFeeRate(uint256 _newFeeRate) external onlyOwner {
        require(_newFeeRate <= 10, "Fee rate too high"); // Max 10%
        platformFeeRate = _newFeeRate;
        emit PlatformFeeUpdated(_newFeeRate);
    }

    /**
     * @dev Update fee recipient
     * @param _newFeeRecipient New fee recipient address
     */
    function updateFeeRecipient(address _newFeeRecipient) external onlyOwner {
        require(_newFeeRecipient != address(0), "Invalid fee recipient");
        feeRecipient = _newFeeRecipient;
    }

    /**
     * @dev Get total bills count
     * @return Total bill count
     */
    function getTotalBills() external view returns (uint256) {
        return _billIds.current();
    }

    /**
     * @dev Get total payments count
     * @return Total payment count
     */
    function getTotalPayments() external view returns (uint256) {
        return _paymentIds.current();
    }

    /**
     * @dev Emergency withdraw function for owner
     */
    function emergencyWithdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
}

